import React from "react";
import { Link, BrowserRouter, Route } from "react-router-dom";
import fire from "./database";

var state = {};
const handleName = e => {
  this.setState({
    name: e.target.value
  });
};

const handleAddress = e => {
  this.setState({
    address: e.target.value
  });
};
const handlePExp = e => {
  this.setState({
    pExp: e.target.value
  });
  var key = fire
    .database()
    .ref("users")
    .push().key;
  console.log(key);
};

const handlePLanguages = e => {
  this.setState({
    pLanguages: e.target.value
  });
};

const handlePhone = e => {
  this.setState({
    phone: e.target.value
  });
};

const handleSkills = e => {
  this.setState({
    skills: e.target.value
  });
};

const getLink = () => {
  const { name, address, phone, pExp, pLanguages, skills } = this.state;

  var fLink =
    "/res/" +
    name +
    "/" +
    address +
    "/" +
    phone +
    "/" +
    pExp +
    "/" +
    pLanguages +
    "/" +
    skills;
  this.sendToDB();
  window.location.href = fLink;
};

const sendToDB = e => {
  const { name, address, phone, pExp, pLanguages, skills } = this.state;

  fire
    .database()
    .ref("users/-LxqtghudGKrVOt4Ci9-/Cvs")
    .push({
      name: name,
      address: address,
      phone: phone,
      pExp: pExp,
      pLanguages: pLanguages,
      skills: skills
    });
};

const survey = ({
  match,
  sendToDB,
  state,
  handleAddress,
  handleName,
  handlePExp,
  handlePLanguages,
  handlePhone,
  handleSkills
}) => {
  return (
    <div className="App-header">
      <p>Fill in the following survey</p>
      <h2>JUST FOR TESTING</h2>
      <input
        className="txtboxes"
        placeholder="Enter your name"
        onChange={this.handleName}
        value={this.state.name}
      />
      <br />
      <textarea
        className="txtboxes_big"
        placeholder="Enter your address:"
        onChange={this.handleAddress}
        value={this.state.address}
      ></textarea>
      <br />
      <input
        className="txtboxes"
        placeholder="Enter your Phone number"
        onChange={this.handlePhone}
        value={this.state.phone}
      />
      <textarea
        className="txtboxes_big"
        placeholder="Enter your skills"
        onChange={this.handleSkills}
        value={this.state.skills}
      ></textarea>

      <textarea
        className="txtboxes_big"
        placeholder="Enter your programming languages"
        onChange={this.handlePLanguages}
        value={this.state.pLanguages}
      ></textarea>

      <textarea
        className="txtboxes_big"
        placeholder="Enter your professional experiances"
        onChange={this.handlePExp}
        value={this.state.pExp}
      ></textarea>

      <template />

      <br />
      <button className="button_a" onClick={this.getLink}>
        GO TO THE NEXT PAGE.
      </button>
    </div>
  );
};

export default survey;
