import React, { Component } from "react";
import Firebase from "../FBConfig";

class Res1 extends Component {
  state = {};
  componentDidMount() {
    var key = this.props.match.params.key;

    Firebase.database()
      .ref("CV/" + key)
      .on("value", list => {
        var object = list.val();
        var name = object.Name;
        var address = object.Address;
        var PT = object.PT;
        var PhoneNumber = object.PN;
        var Gmail = object.Gmail;
        var Facebook = object.Facebook;
        var Insta = object.Insta;
        var Major1 = object.Major1;
        var NameOfUniversity1 = object.NOU1;
        var Degree1 = object.Degree1;
        var edudate1 = object.edudate1;
        var Major2 = object.Major2;
        var NameOfUniversity2 = object.NOU2;
        var Degree2 = object.Degree2;
        var edudate2 = object.edudate2;
        var s1 = object.s1;
        var s2 = object.s2;
        var s3 = object.s3;
        var s4 = object.s4;
        var Profile = object.Profile;
        var j1 = object.j1;
        var j1d = object.j1d;
        var j2 = object.j2;
        var j2d = object.j2d;
        var j3 = object.j3;
        var j3d = object.j3d;
        var j4 = object.j4;
        var j4d = object.j4d;
        var ProfileP = object.profileP;

        this.setState({
          name: name,
          address: address,
          PT: PT,
          PhoneNumber: PhoneNumber,
          Gmail: Gmail,
          Facebook: Facebook,
          Insta: Insta,
          Major1: Major1,
          NameOfUniversity1: NameOfUniversity1,
          Degree1: Degree1,
          edudate1: edudate1,
          Major2: Major2,
          NameOfUniversity2: NameOfUniversity2,
          Degree2: Degree2,
          edudate2: edudate2,
          s1: s1,
          s2: s2,
          s3: s3,
          s4: s4,
          Profile: Profile,
          j1: j1,
          j1: j1d,
          j2: j2,
          j2d: j2d,
          j3: j3,
          j3d: j3d,
          j4: j4,
          j4d: j4d,
          avatar: ProfileP
        });
      });
  }
  render() {
    return (
      <div className="App-header">
        <div className="LogoAndName">
          <img className="dev_logo" src={this.state.avatar} />
          <h3>{this.state.name} </h3>
          <h5>
            <h3>
              <br />
            </h3>
            {this.state.address} | {this.state.Gmail} | {this.state.PhoneNumber}{" "}
          </h5>
          <hr className="line" />
        </div>

        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Proffesional title</p>
            <h5>{this.state.PT} </h5>
          </div>
          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major1} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Name of university</p>
            <h5>{this.state.NameOfUniversity1} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Degrees</p>
            <h5>{this.state.Degree1} </h5>
          </div>
          <div className="CvElmnt">
            <p className="Cv_title">Date</p>
            <h5>{this.state.edudate1} </h5>
          </div>
          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major2} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Name of university</p>
            <h5>{this.state.NameOfUniversity2} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Degrees</p>
            <h5>{this.state.Degree2} </h5>
          </div>
          <div className="CvElmnt">
            <p className="Cv_title">Date</p>
            <h5>{this.state.edudate2} </h5>
          </div>
        </div>
        <div className="CvElmnt">
          <p className="Cv_title">s1</p>
          <h5>{this.state.s1} </h5>
        </div>
        <div className="CvElmnt">
          <p className="Cv_title">s2</p>
          <h5>{this.state.s2} </h5>
        </div>
        <div className="CvElmnt">
          <p className="Cv_title">s3</p>
          <h5>{this.state.s3} </h5>
        </div>
        <div className="CvElmnt">
          <p className="Cv_title">s4</p>
          <h5>{this.state.s4} </h5>
        </div>
      </div>
    );
  }
}
export default Res1;
