import React, { Component } from "react";
import "./main.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
export default class Survey1 extends Component {
  state = {
    name: "",
    PT: "",
    PN: "",
    gmail: "",
    address: "",
    in: "",
    major: "",
    NOU: "",
    degree: "",
    skills: "",
    profile: "",
    WE: "",
    JT: ""
  };
  handleName = e => {
    this.setState({
      name: e.target.value
    });
  };

  handlePT = e => {
    this.setState({
      PT: e.target.value
    });
  };
  handelPM = e => {
    this.setState({
      PM: e.target.value
    });
  };
  handelgmail = e => {
    this.setState({
      gmail: e.target.value
    });
  };
  handeladdress = e => {
    this.setState({
      address: e.target.value
    });
  };
  handelin = e => {
    this.setState({
      in: e.target.value
    });
  };
  handelmajor = e => {
    this.setState({
      major: e.target.value
    });
  };
  handelNOU = e => {
    this.setState({
      NOU: e.target.value
    });
  };
  handeldegree = e => {
    this.setState({
      degree: e.target.value
    });
  };
  handelskills = e => {
    this.setState({
      skills: e.target.value
    });
  };
  handelprofile = e => {
    this.setState({
      profile: e.target.value
    });
  };
  handelWE = e => {
    this.setState({
      WE: e.target.value
    });
  };
  handelJT = e => {
    this.setState({
      JT: e.target.value
    });
  };

  getLink = () => {
    var Name = this.state.name;
    var PT = this.state.PT;
    var PM = this.state.PM;
    var Gmail = this.state.gmail;
    var Address = this.state.address;
    var In = this.state.in;
    var Major = this.state.major;
    var NOU = this.state.NOU;
    var Degree = this.state.degree;
    var Skills = this.state.skills;
    var Profile = this.state.profile;
    var WE = this.state.WE;
    var JT = this.state.JT;

    var fLink = "/res/" + firstName + "/" + middleName + "/" + lastName;
    window.location.href = fLink;
  };
  render() {
    return (
      <div className="App-header">
        <p>Fill in the following survey</p>
        <h2>JUST FOR TESTING</h2>
        <input
          className="txtboxes"
          placeholder="Enter your First name"
          onChange={this.handleFirst}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Middle name:"
          onChange={this.handelMiddle}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Last name"
          onChange={this.handelLast}
        />
        <br />
        <button className="button_a" onClick={this.getLink}>
          GO TO THE NEXT PAGE.
        </button>
      </div>
    );
  }
}
