import React, { Component } from "react";
import "./main.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
export default class Survey1 extends Component {
     state = { name: "", proffessional_title: "", phone_number: "", gmail: '', adress: '', linkdedin: "", major: '', Name_of_u };

     handleFirst = e => {
          this.setState({
               first: e.target.value
          });
     };

     handelMiddle = e => {
          this.setState({
               middle: e.target.value
          });
     };
     handelLast = e => {
          console.log(e.target.value);
          // this.getLink();
          this.setState({
               last: e.target.value
          });
          // this.getLink();
     };

     getLink = () => {
          var firstName = this.state.first;
          var middleName = this.state.middle;
          var lastName = this.state.last;
          var fLink = "/res/" + firstName + "/" + middleName + "/" + lastName;
          window.location.href = fLink;
     };
     render() {
          return (
               <div className="App-header">
                    <p>Fill in the following survey</p>
                    <h2>JUST FOR TESTING</h2>
                    <input
                         className="txtboxes"
                         placeholder="Enter your First name"
                         onChange={this.handleFirst}
                    />
                    <br />
                    <input
                         className="txtboxes"
                         placeholder="Enter your Middle name:"
                         onChange={this.handelMiddle}
                    />
                    <br />
                    <input
                         className="txtboxes"
                         placeholder="Enter your Last name"
                         onChange={this.handelLast}
                    />
                    <br />
                    <button className="button_a" onClick={this.getLink}>
                         GO TO THE NEXT PAGE.
        </button>
               </div>
          );
     }
}
