import React, { Component } from "react";
import logo from "./logo.png";
import "./main.css";
import fire from "./database";
import { BrowserRouter, Route, Switch, Link, Redirect } from "react-router-dom";

export default class Home extends Component {


  render() {
    return (

      <div className="App">
        <header className="App-header">
          <h1>Easily create an efficient and persuasive CV</h1>
          <p className="Aboutcvc">
            CV Constructor is a website that allows you to create a
            persuasive CV by choosing a template (out of 5)
            then filling it with your information, then you can
             save as an image or get a link for emailing.
        </p>

          <Link className="button_a" to="/SelectTemplate">
            Start proccess!
          </Link>
        </header>
      </div>
    );
  }
}
