import React, { Component } from "react";
import Firebase from "./database";

class Result1 extends Component {
  state = {};
  componentDidMount() {
    var key = this.props.match.params.key;

    Firebase.database()
      .ref("CV/" + key)
      .on("value", list => {
        var object = list.val();
        var name = object.Name;
        var address = object.Address;
        var PT = object.PT;
        var PhoneNumber = object.PN;
        var Gmail = object.Gmail;
        var Major = object.Major;
        var NameOfUniversity = object.NOU;
        var Degree = object.Degree;
        var Skill1 = object.Skill1;
        var Skill2 = object.Skill2;
        var Skill3 = object.Skill3;
        var Skill4 = object.Skill4;

        var Profile = object.Profile;
        var WE1 = object.WE1;
        var WE2 = object.WE2;
        var WE3 = object.WE3;
        var WE4 = object.WE4;
        var JT = object.JT;
        var In = object.In;
        var ProfileP = object.profileP;

        this.setState({
          name: name,
          address: address,
          PT: PT,
          PhoneNumber: PhoneNumber,
          Gmail: Gmail,
          Major: Major,
          NameOfUniversity: NameOfUniversity,
          Degree: Degree,
          Skill1: Skill1,
          Skill2: Skill2,
          Skill3: Skill3,
          Skill4: Skill4,
          Profile: Profile,
          WE1: WE1,
          WE2: WE2,
          WE3: WE3,
          WE4: WE4,
          JT: JT,
          In: In,
          avatar: ProfileP
        });
      });
  }
  render() {
    return (
      <div className="App-header">
        <div className="LogoAndName">
          <img className="dev_logo" src={this.state.avatar} />
          <h3>{this.state.name} </h3>
          <h5>
            <h3>
              <br />
            </h3>
            {this.state.address} | {this.state.Gmail} | {this.state.PhoneNumber}{" "}
          </h5>
          <hr className="line" />
        </div>

        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Proffesional title</p>
            <h5>{this.state.PT} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Name of university</p>
            <h5>{this.state.NameOfUniversity} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Degrees</p>
            <h5>{this.state.Degree} </h5>
          </div>
        </div>
        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Skills</p>
            <ol>
              <li>{this.state.Skill1} </li>
              <li>{this.state.Skill2} </li>
              <li>{this.state.Skill3} </li>
              <li>{this.state.Skill4} </li>

            </ol>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Work experiance</p>
            <ol>
              <li>{this.state.WE1} </li>
              <li>{this.state.WE2} </li>
              <li>{this.state.WE3} </li>
              <li>{this.state.WE4} </li>
            </ol>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Jop title</p>

            <h5>{this.state.JT} </h5>
          </div>

        </div>

        <a className='FooterObj' onClick={this.GetImg} >Download your CV</a>
        {/* </div><a className='FooterObj' onClick={this.GetImg} >Save to database and get link</a> */}

        <div className="Footer">
          <a className='FooterObj' href={"/template1/" + this.props.match.params.key} >Template1</a>
          <a className='FooterObj' href={"/template2/" + this.props.match.params.key} >Template2</a>
          <a className='FooterObj' href={"/template3/" + this.props.match.params.key} > Template3</a>
          <a className='FooterObj' href={"/template4/" + this.props.match.params.key} >Template4</a>
          <a className='FooterObj' href={"/template5/" + this.props.match.params.key} >Template5</a>

        </div>

      </div>
    );
  }
}
export default Result1;
