import React, { Component } from "react";
export default class Survey2 extends Component {
  state = {
    Name: "",
    PN: "",
    Gmail: "",
    Address: "",
    In: "",
    NOU: "",
    Skills: "",
    Profile: "",
    WE: ""
  };
  handlename = e => {
    this.setState({
      Name: e.target.value
    });
  };

  handlePT = e => {
    this.setState({
      PT: e.target.value
    });
  };
  handlePN = e => {
    this.setState({
      PN: e.target.value
    });
  };
  handlegmail = e => {
    this.setState({
      Gmail: e.target.value
    });
  };
  handleaddress = e => {
    this.setState({
      Address: e.target.value
    });
  };
  handlein = e => {
    this.setState({
      In: e.target.value
    });
  };
  handlemajor = e => {
    this.setState({
      Major: e.target.value
    });
  };
  handleNOU = e => {
    this.setState({
      NOU: e.target.value
    });
  };
  handledegree = e => {
    this.setState({
      Degree: e.target.value
    });
  };
  handleskills = e => {
    this.setState({
      Skills: e.target.value
    });
  };
  handleprofile = e => {
    this.setState({
      Profile: e.target.value
    });
  };
  handleWE = e => {
    this.setState({
      WE: e.target.value
    });
  };
  handleJT = e => {
    this.setState({
      JT: e.target.value
    });
  };

  getLink = () => {
    var Name = this.state.Name;
    var PT = this.state.PT;
    var PN = this.state.PN;
    var Gmail = this.state.Gmail;
    var Address = this.state.Address;
    var In = this.state.In;
    var Major = this.state.Major;
    var NOU = this.state.NOU;
    var Degree = this.state.Degree;
    var Skills = this.state.Skills;
    var Profile = this.state.Profile;
    var WE = this.state.WE;
    var JT = this.state.JT;

    var fLink =
      "/res/" +
      Name +
      "/" +
      PT +
      "/" +
      PN +
      "/" +
      Gmail +
      "/" +
      Address +
      "/" +
      In +
      "/" +
      Major +
      "/" +
      NOU +
      "/" +
      Degree +
      "/" +
      Skills +
      "/" +
      Profile +
      "/" +
      WE +
      "/" +
      JT;

    window.location.href = fLink;
  };
  render() {
    return (
      <div className="App-header">
        <p>Fill in the following survey</p>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handlename}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Proffesional title"
          onChange={this.handlePT}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Phone Number"
          onChange={this.handlePN}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Gmial address"
          onChange={this.handlegmail}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Address"
          onChange={this.handleaddress}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your IN"
          onChange={this.handlein}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your major"
          onChange={this.handlemajor}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter the name of ypur university"
          onChange={this.handleNOU}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your degree"
          onChange={this.handledegree}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your skills"
          onChange={this.handleskills}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Profile"
          onChange={this.handleprofile}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your work experience"
          onChange={this.handleWE}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your job title"
          onChange={this.handleJT}
        />
        <br />
        <button className="button_a" onClick={this.getLink}>
          GO TO THE NEXT PAGE.
        </button>
      </div>
    );
  }
}
