import React, { Component } from "react";
import "./main.css";
import Firebase from "../FBConfig";
import FileUploader from "react-firebase-file-uploader";

export default class Survey1 extends Component {
  state = {
    Name: "",
    PT: "",
    PN: "",
    Gmail: "",
    Address: "",
    Facebook: "",
    Insta: "",
    Major1: "",
    NOU1: "",
    Degree1: "",
    edudate1: "",
    Major2: "",
    NOU2: "",
    Degree2: "",
    edudate2: "",
    s1: "",
    s2: "",
    s3: "",
    s4: "",
    j1: "",
    j1d: "",
    j2: "",
    j2d: "",
    j3: "",
    j3d: "",
    j4: "",
    j4d: "",
    loadingMessege: ""
  };

  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.log(error);
  };
  handleUploadSuccess = filename => {
    Firebase.storage()
      .ref("images")
      .child(filename)
      .getDownloadURL()
      .then(url => {
        this.setState({ avatarURL: url });
      });
  };

  handlename = e => {
    this.setState({
      Name: e.target.value
    });
  };

  handlePT = e => {
    this.setState({
      PT: e.target.value
    });
  };
  handlePN = e => {
    this.setState({
      PN: e.target.value
    });
  };
  handlegmail = e => {
    this.setState({
      Gmail: e.target.value
    });
  };
  handleaddress = e => {
    this.setState({
      Address: e.target.value
    });
  };
  handleFacebook = e => {
    this.setState({
      Facebook: e.target.value
    });
  };
  handleInsta = e => {
    this.setState({
      Insta: e.target.value
    });
  };
  handlemajor1 = e => {
    this.setState({
      Major1: e.target.value
    });
  };
  handleNOU1 = e => {
    this.setState({
      NOU1: e.target.value
    });
  };
  handleedudate1 = e => {
    this.setState({
      edudate1: e.target.value
    })
  }
  handledegree1 = e => {
    this.setState({
      Degree1: e.target.value
    });
  };
  handlemajor2 = e => {
    this.setState({
      Major2: e.target.value
    });
  };
  handleNOU2 = e => {
    this.setState({
      NOU2: e.target.value
    });
  };
  handleedudate2 = e => {
    this.setState({
      edudate2: e.target.value
    })
  }
  handledegree2 = e => {
    this.setState({
      Degree2: e.target.value
    });
  };
  handles1 = e => {
    this.setState({
      s1: e.target.value
    });
  };
  handles2 = e => {
    this.setState({
      s2: e.target.value
    });
  };
  handles3 = e => {
    this.setState({
      s3: e.target.value
    });
  };
  handles4 = e => {
    this.setState({
      s4: e.target.value
    });
  };
  handlej1 = e => {
    this.setState({
      j1: e.target.value
    });
  };
  handlej1d = e => {
    this.setState({
      j1d: e.target.value
    });
  };
  handlej2 = e => {
    this.setState({
      j2: e.target.value
    });
  };
  handlej2d = e => {
    this.setState({
      j2d: e.target.value
    });
  };
  handlej3 = e => {
    this.setState({
      j3: e.target.value
    });
  }; handlej3d = e => {
    this.setState({
      j3d: e.target.value
    });
  }; handlej4 = e => {
    this.setState({
      j4: e.target.value
    });
  }; handlej4d = e => {
    this.setState({
      j4d: e.target.value
    });
  };
  MoveToFB = () => {
    var key = Firebase.database()
      .ref("CV")
      .push({
        Name: this.state.Name,
        PT: this.state.PT,
        PN: this.state.PN,
        Gmail: this.state.Gmail,
        Address: this.state.Address,
        Facebook: this.state.Facebook,
        Insta: this.state.Insta,
        Major1: this.state.Major1,
        NOU1: this.state.NOU1,
        Degree1: this.state.Degree1,
        edudate1: this.state.edudate1,
        Major2: this.state.Major2,
        NOU2: this.state.NOU2,
        Degree2: this.state.Degree2,
        edudate2: this.state.edudate2,
        s1: this.state.s1,
        s2: this.state.s2,
        s3: this.state.s3,
        s4: this.state.s4,
        j1: this.state.j1,
        j1d: this.state.j1d,
        j2: this.state.j2,
        j2d: this.state.j2d,
        j3: this.state.j3,
        j3d: this.state.j3d,
        j4: this.state.j4,
        j4d: this.state.j4d,
      }).key;
    var finalLink = "./result1/" + key;
    this.setState({ loadingMessege: "Loading your CV..." });
    setTimeout(() => {
      window.location.href = finalLink;
    }, 2000);
  };
  render() {
    return (
      <div className="App-header">
        <h1>Fill in the following survey</h1>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handlename}
        />
        <FileUploader
          className="uploader"
          accept="image/*"
          name="avatar"
          placeholder="select image"
          randomizeFilename
          storageRef={Firebase.storage().ref("images")}
          onUploadError={this.handleUploadError}
          onUploadSuccess={this.handleUploadSuccess}
        />

        <br />

        <input
          className="txtboxes"
          placeholder="Enter your Proffesional title"
          onChange={this.handlePT}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Phone Number"
          onChange={this.handlePN}
        />
