import React from "react";
import { Link, BrowserRouter, Route } from "react-router-dom";
import fire from "./database";

state = {};
handleName = e => {
  this.setState({
    name: e.target.value
  });
};

handleAddress = e => {
  this.setState({
    address: e.target.value
  });
};
handlePExp = e => {
  this.setState({
    pExp: e.target.value
  });
  var key = fire
    .database()
    .ref("users")
    .push().key;
  console.log(key);
};

handlePLanguages = e => {
  this.setState({
    pLanguages: e.target.value
  });
};

handlePhone = e => {
  this.setState({
    phone: e.target.value
  });
};

handleSkills = e => {
  this.setState({
    skills: e.target.value
  });
};

getLink = () => {
  const { name, address, phone, pExp, pLanguages, skills } = this.state;

  var fLink =
    "/res/" +
    name +
    "/" +
    address +
    "/" +
    phone +
    "/" +
    pExp +
    "/" +
    pLanguages +
    "/" +
    skills;
  this.sendToDB();
  window.location.href = fLink;
};

sendToDB = e => {
  const { name, address, phone, pExp, pLanguages, skills } = this.state;

  fire
    .database()
    .ref("users/-LxqtghudGKrVOt4Ci9-/Cvs")
    .push({
      name: name,
      address: address,
      phone: phone,
      pExp: pExp,
      pLanguages: pLanguages,
      skills: skills
    });
};

const survey = ({ match }) => {
  return <div>Worked</div>;
};

export default survey;
