import React, { Component } from "react";
import "./main.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
import fire from "./database";

export default class Survey extends Component {
  state = { first: "", middle: "", last: "" };
  handleName = e => {
    this.setState({
      name: e.target.value
    });
  };

  componentWillMount() {
    fire
      .database()
      .ref("users")
      .on("value", snapshot => {
        const id = snapshot.id;
        const users = snapshot.val();
        console.log(Object.keys(users));
      });
  }

  handleAddress = e => {
    this.setState({
      address: e.target.value
    });
  };
  handlePExp = e => {
    this.setState({
      pExp: e.target.value
    });
  };

  handlePLanguages = e => {
    this.setState({
      pLanguages: e.target.value
    });
  };

  handlePhone = e => {
    this.setState({
      phone: e.target.value
    });
  };

  getLink = () => {
    var firstName = this.state.first;
    var middleName = this.state.middle;
    var lastName = this.state.last;
    var fLink = "/res/" + firstName + "/" + middleName + "/" + lastName;
    this.sendToDB();
    window.location.href = fLink;
  };

  sendToDB = e => {
    var key = fire
      .database()
      .ref("users")
      .push().key;
    console.log(key);
    fire
      .database()
      .ref("users/-LxqtghudGKrVOt4Ci9-/Cvs")
      .push({
        firstName: this.state.first,
        middleName: this.state.middle,
        lastName: this.state.last
      });
  };

  render() {
    return (
      <div className="App-header">
        <template />

        <p>Fill in the following survey</p>
        <h2>JUST FOR TESTING</h2>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handleName}
          value={this.state.name}
        />
        <br />
        <textarea
          className="txtboxes_big"
          placeholder="Enter your address:"
          onChange={this.handleAddress}
          value={this.state.address}
        ></textarea>
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Phone number"
          onChange={this.handlePhone}
          value={this.state.phone}
        />
        <textarea
          className="txtboxes_big"
          placeholder="Enter your skills"
          onChange={this.handleSkills}
          value={this.state.skills}
        ></textarea>

        <textarea
          className="txtboxes_big"
          placeholder="Enter your programming languages"
          onChange={this.handlePLanguages}
          value={this.state.pLanguages}
        ></textarea>

        <textarea
          className="txtboxes_big"
          placeholder="Enter your professional experiances"
          onChange={this.handlePExp}
          value={this.state.pExp}
        ></textarea>

        <br />
        <button className="button_a" onClick={this.getLink}>
          GO TO THE NEXT PAGE.
        </button>
      </div>
    );
  }
}

export const template = ({ match }) => {
  console.log(match.params.temp);
};
