import React, { Component } from "react";
import Firebase from "./database";
import './main.css';
import FileUploader from "react-firebase-file-uploader";
import { saveAs } from 'file-saver';
import html2canvas from 'html2canvas';

class Survey3 extends Component {
    state = {
        Name: "",
        PT: "",
        PN: "",
        Gmail: "",
        Address: "",
        Facebook: "",
        Major: "",
        NOU: "",
        Degree: "",
        Skills: "",
        Profile: "",
        WE: "",
        JT: "",
        loadingMessege: ""
    }
    handlename = e => {
        this.setState({
            Name: e.target.value
        });
    };

    handlePT = e => {
        this.setState({
            PT: e.target.value
        });
    };
    handlePN = e => {
        this.setState({
            PN: e.target.value
        });
    };
    handlegmail = e => {
        this.setState({
            Gmail: e.target.value
        });
    };
    handleaddress = e => {
        this.setState({
            Address: e.target.value
        });
    };
    handleFacebook = e => {
        this.setState({
            Facebook: e.target.value
        });
    };
    handlemajor = e => {
        this.setState({
            Major: e.target.value
        });
    };
    handleNOU = e => {
        this.setState({
            NOU: e.target.value
        });
    };
    handledegree = e => {
        this.setState({
            Degree: e.target.value
        });
    };
    handleskill1 = e => {
        this.setState({
            Skill1: e.target.value
        });
    };
    handleskill2 = e => {
        this.setState({
            Skill2: e.target.value
        });
    };

    handleskill3 = e => {
        this.setState({
            Skill3: e.target.value
        });
    };
    handleskill4 = e => {
        this.setState({
            Skill4: e.target.value
        });
    };
    handleprofile = e => {
        this.setState({
            Profile: e.target.value
        });
    };
    handleWE1 = e => {
        this.setState({
            WE1: e.target.value
        });
    };

    handleWE2 = e => {
        this.setState({
            WE2: e.target.value
        });
    };

    handleWE3 = e => {
        this.setState({
            WE3: e.target.value
        });
    };

    handleWE4 = e => {
        this.setState({
            WE4: e.target.value
        });
    };
    handleJT = e => {
        this.setState({
            JT: e.target.value
        });
    };
    GetImg = () => {

        html2canvas(document.body).then(function (canvas) {
            document.body.appendChild(canvas);
            var img = canvas.toDataURL()
            saveAs(img, "pretty image.png");


        });
    };


    handleUploadError = error => {
        this.setState({ isUploading: false });
        console.log(error);
    };
    handleUploadSuccess = filename => {
        Firebase.storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => {
                this.setState({ avatar: url });
                console.log(this.state.avatar)
            });
    };

    componentDidMount() {
        Firebase.database()
            .ref("CV/")
            .on("value", list => {
                var object = list.val();
                var ProfileP = object.profileP;

                this.setState({
                    avatar: ProfileP
                });
            });
    }



    SavetoDatabase = () => {
        var pushedData = Firebase.database()
            .ref("CV/")
            .push({
                Name: this.state.Name,
                PT: this.state.PT,
                PN: this.state.PN,
                Gmail: this.state.Gmail,
                Address: this.state.Address,
                Facebook: this.state.Facebook,
                Major: this.state.Major,
                NOU: this.state.NOU,
                Degree: this.state.Degree,
                Skill1: this.state.Skill1,
                Skill2: this.state.Skill2,
                Skill3: this.state.Skill3,
                Skill4: this.state.Skill4,

                Profile: this.state.Profile,
                WE1: this.state.WE1,
                WE2: this.state.WE2,
                WE3: this.state.WE3,
                WE4: this.state.WE4,
                JT: this.state.JT,
                profileP: this.state.avatar
            }).then((pushedData) => {
                console.log(pushedData.key)
                var finalLink = "/template3/" + pushedData.key;
                console.log(finalLink)
                console.log(this.state.avatar)
                window.location.href = finalLink;

            })

    };

    render() {
        return (
            <div className="template3MainDiv">


                <div className='template3SecondaryDiv' >
                    <img className="dev_logoT2" src={this.state.avatar} />
                    <FileUploader
                        className="uploader"
                        accept='image/*'
                        name='avatar'
                        placeholder='sellect your avatar'
                        storageRef={Firebase.storage().ref("images")}
                        onUploadError={this.handleUploadError}
                        onUploadSuccess={this.handleUploadSuccess}
                    />

                    <input
                        className='txtInputTemplate4'
                        placeholder="Enter Your Name"
                        onChange={this.handlename}
                    />
                    <hr />


                    <input
                        className='txtInputTemplate4'
                        placeholder="Enter Your Address"
                        onChange={this.handleaddress}
                    />
                    <hr />




                    <input
                        className='txtInputTemplate4'
                        placeholder='Enter Your PhoneNumber:'
                        onChange={this.handlePN}

                    />
                    <hr />


                    <input
                        className='txtInputTemplate4'
                        placeholder='Enter Your Email:'
                        onChange={this.handlegmail}

                    />

                    <hr />


                </div>


                <div className='template2MainDivElmnts'>


                    <div className="AllCvs">
                        <div className="CvElmntLight">
                            <p className="Cv_titleLight">Proffesional title</p>
                            <input
                                className='txtInputTemplate4'
                                placeholder='Your Proffesional title:'
                                onChange={this.handlePT}

                            />            </div>

                        <div className="CvElmntLight">
                            <p className="Cv_titleLight">Major</p>
                            <input
                                className='txtInputTemplate4'
                                placeholder='Your university major:'
