import axios, { post } from "axios";
import React, { Component } from "react";
import Firebase from "./database";
import html2canvas from 'html2canvas';
import { saveAs } from 'file-saver';
class Result1 extends Component {
  GetImg = () => {

    html2canvas(document.body).then(function (canvas) {
      document.body.appendChild(canvas);
      var img = canvas.toDataURL()
      saveAs(img, "pretty image.png");


    });
  };
  onChange(e) {
    let files = e.target.files;
    let reader = new FileReader();
    reader.readAsDataURL()
  }
  render() {
    return (
      <div className="App-header">
        <div className="LogoAndName">
          <input type="file" name="file" onChange={(e) => this.onChange(e)} />
          <h3>Your first and last name </h3>
          <h5>
            <h3>
              <br />
            </h3>
            Adress, city, state | yourgmail@gmail.com | +90 7528 753
          </h5>
          <hr className="line" />
        </div>

        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Proffesional title</p>
            <h5>Your Proffesional title </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>Your univerisy major</h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Name of university</p>
            <h5>Name of university studied at</h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Degrees</p>
            <h5>Your university degree</h5>
          </div>
        </div>
        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>Your college major </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Skills</p>
            <ol>
              <li>Your first skill </li>
              <li>Your second skill </li>
              <li>Your third skill </li>
              <li>your fourth skill </li>

            </ol>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Work experiance</p>
            <ol>
              <li>Your first job (2011-2019) </li>
              <li>Your second job (2011-2019)</li>
              <li>Your third job (2011-2019) </li>
              <li>Your fourth job (2011-2019)  </li>
            </ol>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Job title</p>

            <h5>Your job title </h5>
          </div>

        </div>

        <a className='FooterObj' onClick={this.GetImg} >Download your CV</a>


      </div >
    );
  }
}
export default Result1;
