import React, { Component } from "react";
import "./main.css";
import fire from "./database";
class UserCvs extends Component {
  state = { cvs: [] };
  componentWillMount() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({ uid: user.uid });
        console.log(this.state.uid);
        fire
          .database()
          .ref("users/" + user.uid + "/Cvs")
          .on("value", snapshot => {
            console.log(snapshot.val());
            var cvs = snapshot.val();
            this.addCvsToState(cvs);
          });
      }
    });
  }

  addCvsToState = cvs => {
    this.setState({ cvs: cvs });
  };
  render() {
    return (
      <div>
        <h1>User Shits</h1>
        <p> Cvs{this.state.cvs}</p>
      </div>
    );
  }
}

export default UserCvs;
