import React, { Component } from "react";
import fire from "./database";

export default class Login extends Component {
  state = { email: "", password: "" };

  componentWillMount() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        console.log("user logged");
        window.location.href = "/";
      }
    });
  }

  handleSignUp = () => {
    const { email, password } = this.state;
    fire
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => this.props.navigation.navigate("Profile"))
      .catch(error => console.log(error));

    fire
      .database()
      .collection("users")
      .add({ email: email });
  };

  addtodatabase = (path, email, password) => {
    fire.database.ref(path).push(email);
    fire.database.ref(path).push(password);
  };

  handleSignin = () => {
    const { email, password } = this.state;
    console.log(email);
    fire.auth();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .catch(error => console.log(error));
  };

  handleEmail = e => {
    this.setState({
      email: e.target.value
    });
    console.log(e.target.value);
  };
  handlepass = e => {
    this.setState({
      password: e.target.value
    });
  };

  render() {
    return (
      <div>
        <input onChange={this.handleEmail} placeholder="Enter your Email" />
        <br />
        <input onChange={this.handlepass} placeholder="Enter Your password" />
        <br />
        <button onClick={this.handleSignin}>Login</button>
        <br />
        <button
          onClick={this.addtodatabase(
            "users",
            this.state.email,
            this.state.password
          )}
        >
          SignUp
        </button>
      </div>
    );
  }
}
