import React, { Component } from "react";
import "./main.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
import fire from "./database";

export default class Survey extends Component {
  state = {};
  handleName = e => {
    this.setState({
      name: e.target.value
    });
  };

  componentWillMount() {
    fire
      .database()
      .ref("users")
      .on("value", snapshot => {
        const id = snapshot.id;
        const users = snapshot.val();
        console.log(Object.keys(users));
      });
  }

  template = ({ match }) => {
    this.setState({ template: match.params.template });
  };

  handleAddress = e => {
    this.setState({
      address: e.target.value
    });
    console.log(this.state.template);
  };
  handlePExp = e => {
    this.setState({
      pExp: e.target.value
    });
    var key = fire
      .database()
      .ref("users")
      .push().key;
    console.log(key);
  };

  handlePLanguages = e => {
    this.setState({
      pLanguages: e.target.value
    });
  };

  handlePhone = e => {
    this.setState({
      phone: e.target.value
    });
  };

  handleSkills = e => {
    this.setState({
      skills: e.target.value
    });
  };

  getLink = () => {
    const { name, address, phone, pExp, pLanguages, skills } = this.state;

    var fLink =
      "/res/" +
      name +
      "/" +
      address +
      "/" +
      phone +
      "/" +
      pExp +
      "/" +
      pLanguages +
      "/" +
      skills;
    this.sendToDB();
    window.location.href = fLink;
  };

  sendToDB = e => {
    const { name, address, phone, pExp, pLanguages, skills } = this.state;

    fire
      .database()
      .ref("users/-LxqtghudGKrVOt4Ci9-/Cvs")
      .push({
        name: name,
        address: address,
        phone: phone,
        pExp: pExp,
        pLanguages: pLanguages,
        skills: skills
      });
  };

  render() {
    return (
      <div className="App-header">
        <template />

        <p>Fill in the following survey</p>
        <h2>JUST FOR TESTING</h2>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handleName}
          value={this.state.name}
        />
        <br />
        <textarea
          className="txtboxes_big"
          placeholder="Enter your address:"
          onChange={this.handleAddress}
          value={this.state.address}
        ></textarea>
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Phone number"
          onChange={this.handlePhone}
          value={this.state.phone}
        />
        <textarea
          className="txtboxes_big"
          placeholder="Enter your skills"
          onChange={this.handleSkills}
          value={this.state.skills}
        ></textarea>

        <textarea
          className="txtboxes_big"
          placeholder="Enter your programming languages"
          onChange={this.handlePLanguages}
          value={this.state.pLanguages}
        ></textarea>

        <textarea
          className="txtboxes_big"
          placeholder="Enter your professional experiances"
          onChange={this.handlePExp}
          value={this.state.pExp}
        ></textarea>

        <template />

        <br />
        <button className="button_a" onClick={this.getLink}>
          GO TO THE NEXT PAGE.
        </button>
      </div>
    );
  }
}

export const template = ({ match }) => {
  console.log(match.params.temp);
};
