import React, { Component } from "react";
import "./main.css";
import fire from "./database";
class UserCvs extends Component {
  state = {};
  componentWillMount() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({ uid: user.uid });
        console.log(this.state.uid);
        this.setState({ cvs: this.getCvs(user) });
        console.log(this.state.cvs);
      }
    });
  }

  getCvs = user => {
    fire
      .database()
      .ref("users/" + user.uid + "/Cvs")
      .on("value", snapshot => {
        this.setState(snapshot.val());

        return snapshot.val();
      });
  };

  componentDidMount() {
    // fire
