import React, { Component } from "react";
import "./main.css";
import Firebase from "../FBConfig";
import FileUploader from "react-firebase-file-uploader";

export default class Survey1 extends Component {
  state = {
    Name: "",
    PT: "",
    PN: "",
    Gmail: "",
    Address: "",
    Facebook: "",
    Insta: "",
    Major: "",
    NOU: "",
    Degree: "",
    s1: "",
    s2: "",
    s3: "",
    s4: "",
    Profile: "",
    j1: "",
    j1d: "",
    j2: "",
    j2d: "",
    j3: "",
    j3d: "",
    j4: "",
    j4d: "",
    loadingMessege: ""
  };

  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.log(error);
  };
  handleUploadSuccess = filename => {
    Firebase.storage()
      .ref("images")
      .child(filename)
      .getDownloadURL()
      .then(url => {
        this.setState({ avatarURL: url });
      });
  };

  handlename = e => {
    this.setState({
      Name: e.target.value
    });
  };

  handlePT = e => {
    this.setState({
      PT: e.target.value
    });
  };
  handlePN = e => {
    this.setState({
      PN: e.target.value
    });
  };
  handlegmail = e => {
    this.setState({
      Gmail: e.target.value
    });
  };
  handleaddress = e => {
    this.setState({
      Address: e.target.value
    });
  };
  handleFacebook = e => {
    this.setState({
      Facebook: e.target.value
    });
  };
  handleInsta = e => {
    this.setState({
      Insta: e.target.value
    });
  };
  handlemajor = e => {
    this.setState({
      Major: e.target.value
    });
  };
  handleNOU = e => {
    this.setState({
      NOU: e.target.value
    });
  };
  handledegree = e => {
    this.setState({
      Degree: e.target.value
    });
  };
  handles1 = e => {
    this.setState({
      s1: e.target.value
    });
  };
  handles2 = e => {
    this.setState({
      s2: e.target.value
    });
  };
  handles3 = e => {
    this.setState({
      s3: e.target.value
    });
  };
  handles4 = e => {
    this.setState({
      s4: e.target.value
    });
  };

  handleprofile = e => {
    this.setState({
      Profile: e.target.value
    });
  };
  handlej1 = e => {
    this.setState({
      j1: e.target.value
    });
  };
  handlej1d = e => {
    this.setState({
      j1d: e.target.value
    });
  };
  handlej2 = e => {
    this.setState({
      j2: e.target.value
    });
  };
  handlej2d = e => {
    this.setState({
      j2d: e.target.value
    });
  };
  handlej3 = e => {
    this.setState({
      j3: e.target.value
    });
  }; handlej3d = e => {
    this.setState({
      j3d: e.target.value
    });
  }; handlej4 = e => {
    this.setState({
      j4: e.target.value
    });
  }; handlej4d = e => {
    this.setState({
      j4d: e.target.value
    });
  };
  MoveToFB = () => {
    var key = Firebase.database()
      .ref("CV")
      .push({
        Name: this.state.Name,
        PT: this.state.PT,
        PN: this.state.PN,
        Gmail: this.state.Gmail,
        Address: this.state.Address,
        Facebook: this.state.Facebook,
        Insta: this.state.Insta,
        Major: this.state.Major,
        NOU: this.state.NOU,
        Degree: this.state.Degree,
        s1: this.state.s1,
        s2: this.state.s2,
        s3: this.state.s3,
        s4: this.state.s4,
        Profile: this.state.Profile,
        j1: this.state.j1,
        j1d: this.state.j1d,
        j2: this.state.j2,
        j2d: this.state.j2d,
        j3: this.state.j3,
        j3d: this.state.j3d,
        j4: this.state.j4,
        j4d: this.state.j4d,
        profileP: this.state.avatarURL
      }).key;
    var finalLink = "./result1/" + key;
    this.setState({ loadingMessege: "Loading your CV..." });
    setTimeout(() => {
      window.location.href = finalLink;
    }, 2000);
  };
  render() {
    return (
      <div className="App-header">
        <p>Fill in the following survey</p>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handlename}
        />
        <h5>Choose your profile Photo</h5>
        <FileUploader
          className="uploader"
          accept="image/*"
          name="avatar"
          placeholder="select image"
          randomizeFilename
          storageRef={Firebase.storage().ref("images")}
          onUploadError={this.handleUploadError}
          onUploadSuccess={this.handleUploadSuccess}
        />

        <br />

        <input
          className="txtboxes"
          placeholder="Enter your Proffesional title"
          onChange={this.handlePT}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Phone Number"
          onChange={this.handlePN}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Gmail address"
          onChange={this.handlegmail}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Address"
          onChange={this.handleaddress}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Facebook"
          onChange={this.handleFacebook}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Facebook"
          onChange={this.handleInsta}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your major"
          onChange={this.handlemajor}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter the name of your university"
          onChange={this.handleNOU}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your degree"
          onChange={this.handledegree}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your first skill"
          onChange={this.handles1}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your second skill"
          onChange={this.handles2}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your third skill"
          onChange={this.handles3}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your fourth skill"
          onChange={this.handles4}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Profile"
          onChange={this.handleprofile}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter job #1"
          onChange={this.handlej1}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Worked from-to"
          onChange={this.handlej1d}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter job #2"
          onChange={this.handlej2}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Worked from-to"
          onChange={this.handlej2d}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter job #3"
          onChange={this.handlej3}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Worked from-to"
          onChange={this.handlej3d}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Enter job #4"
          onChange={this.handlej4}
        />
        <br />
        <input
          className="txtboxes"
          placeholder="Worked from-to"
          onChange={this.handlej4d}
        />
        <br />

        <button className="button_a" onClick={this.MoveToFB}>
          GET YOUR CV
        </button>
        <h4>{this.state.loadingMessege}</h4>
      </div>
    );
  }
}
