import React, { Component } from "react";
import "./main.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
import fire from "./database";

export default class Survey extends Component {
  state = { first: "", middle: "", last: "" };
  handleFirst = e => {
    this.setState({
      first: e.target.value
    });
  };

  componentWillMount() {
    fire
      .database()
      .ref("users")
      .on("value", snapshot => {
        const id = snapshot.id;
        const users = snapshot.val();
        console.log(Object.keys(users));
      });
  }

  handelMiddle = e => {
    this.setState({
      middle: e.target.value
    });
  };
  handelLast = e => {
    console.log(e.target.value);
    // this.getLink();
    this.setState({
      last: e.target.value
    });
    // this.getLink();
  };

  getLink = () => {
    var firstName = this.state.first;
    var middleName = this.state.middle;
    var lastName = this.state.last;
    var fLink = "/res/" + firstName + "/" + middleName + "/" + lastName;
    this.sendToDB();
    window.location.href = fLink;
  };

  sendToDB = e => {
    fire
      .database()
      .ref("users/-LxqtghudGKrVOt4Ci9-/Cvs")
      .push({
        firstName: this.state.first,
        middleName: this.state.middle,
        lastName: this.state.last
      });
  };

  render() {
    return (
      <div className="App-header">
        <template />

        <p>Fill in the following survey</p>
        <h2>JUST FOR TESTING</h2>
        <input
          className="txtboxes"
          placeholder="Enter your name"
          onChange={this.handleName}
          value={this.state.name}
        />
        <br />
        <textarea
          className="txtboxes_big"
          placeholder="Enter your address:"
          onChange={this.handelAddress}
          value={this.state.address}
        ></textarea>
        <br />
        <input
          className="txtboxes"
          placeholder="Enter your Last name"
          onChange={this.handePhone}
          value={this.state.phone}
        />
        <input
          className="txtboxes"
          placeholder="Enter your Last name"
          onChange={this.handePhone}
          value={this.state.phone}
        />

        <br />
        <button className="button_a" onClick={this.getLink}>
          GO TO THE NEXT PAGE.
        </button>
      </div>
    );
  }
}

export const template = ({ match }) => {
  console.log(match.params.temp);
};
