import React, { Component } from "react";
import Firebase from "../FBConfig";

class Res1 extends Component {
  state = {};
  componentDidMount() {
    var key = this.props.match.params.key;

    Firebase.database()
      .ref("CV/" + key)
      .on("value", list => {
        var object = list.val();
        var name = object.Name;
        var address = object.Address;
        var PT = object.PT;
        var PhoneNumber = object.PN;
        var Gmail = object.Gmail;
        var Major1 = object.Major1;
        var NameOfUniversity1 = object.NOU1;
        var Degree1 = object.Degree1;
        var edudate = object.edudate1;
        var Major2 = object.Major2;
        var NameOfUniversity2 = object.NOU2;
        var Degree2 = object.Degree2;
        var edudate2 = object.edudate2;
        var s1 = object.s1;
        var s2 = object.s2;
        var s3 = object.s3;
        var s4 = object.s4;
        var Profile = object.Profile;
        var j1 = object.j1;
        var j1d = object.j1d;
        var j2 = object.j2;
        var j2d = object.j2d;
        var j3 = object.j3;
        var j3d = object.j3d;
        var j4 = object.j4;
        var j4d = object.j4d;
        var ProfileP = object.profileP;

        this.setState({
          name: name,
          address: address,
          PT: PT,
          PhoneNumber: PhoneNumber,
          Gmail: Gmail,
          Major: Major,
          NameOfUniversity: NameOfUniversity,
          Degree: Degree,
          Skills: Skills,
          Profile: Profile,
          WE: WE,
          JT: JT,
          In: In,
          avatar: ProfileP
        });
      });
  }
  render() {
    return (
      <div className="App-header">
        <div className="LogoAndName">
          <img className="dev_logo" src={this.state.avatar} />
          <h3>{this.state.name} </h3>
          <h5>
            <h3>
              <br />
            </h3>
            {this.state.address} | {this.state.Gmail} | {this.state.PhoneNumber}{" "}
          </h5>
          <hr className="line" />
        </div>

        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Proffesional title</p>
            <h5>{this.state.PT} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Name of university</p>
            <h5>{this.state.NameOfUniversity} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Degrees</p>
            <h5>{this.state.Degree} </h5>
          </div>
        </div>
        <div className="AllCvs">
          <div className="CvElmnt">
            <p className="Cv_title">Major</p>
            <h5>{this.state.Major} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Skills</p>
            <h5>{this.state.Skills} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Work experiance</p>
            <h5>{this.state.WE} </h5>
          </div>

          <div className="CvElmnt">
            <p className="Cv_title">Jop title</p>

            <h5>{this.state.JT} </h5>
          </div>
        </div>
      </div>
    );
  }
}
export default Res1;
